mapper.select:
SELECT
	record.*,
	hybrid.name as hybrid_name,
	plant.code as plant_code,
	plant.code_one as plant_code_one,
	plant.code_two as plant_code_two
FROM
	os_plant_record as record, os_hybrid as hybrid, os_plant as plant
WHERE
	record.hybrid_id = hybrid.id AND record.plant_id = plant.id AND record.create_by_id = #{userId} 

		 AND record.hybrid_id = #{hybridId}


		 AND record.plant_id = #{plantId}


		 AND record.create_time >= #{startTime}


		 AND record.create_time <= #{endTime}

ORDER BY record.id DESC

SELECT record.*, hybrid.name as hybrid_name, plant.code as plant_code, plant.code_one as plant_code_one, plant.code_two as plant_code_two FROM os_plant_record as record left join sys_user u on u.user_id = record.create_by_id left join sys_dept d on u.dept_id = d.dept_id, os_hybrid as hybrid, os_plant as plant WHERE record.hybrid_id = hybrid.id AND record.plant_id = plant.id
AND record.hybrid_id = #{hybridId}
AND record.plant_id = #{plantId}
AND record.create_time >= #{startTime}
AND record.create_time <= #{endTime}
${dataScope} ORDER BY record.id DESC
