mapper.select:
SELECT
	record.*,
	germplasm.name as germplasm_name,
	seedling.code as seedling_code,
	seedling.code_one as seedling_code_one,
	seedling.code_two as seedling_code_two
FROM
	os_seedling_record as record, os_germplasm as germplasm, os_seedling as seedling
WHERE
	record.germplasm_id = germplasm.id AND record.seedling_id = seedling.id AND record.create_by_id = #{userId}

		 AND record.germplasm_id = #{germplasmId}


		 AND record.seedling_id = #{seedlingId}


		 AND record.create_time >= #{startTime}


		 AND record.create_time <= #{endTime}

ORDER BY record.id DESC

SELECT record.*, germplasm.name as germplasm_name, seedling.code as seedling_code, seedling.code_one as seedling_code_one, seedling.code_two as seedling_code_two FROM os_seedling_record as record, os_germplasm as germplasm, os_seedling as seedling WHERE record.germplasm_id = germplasm.id AND record.seedling_id = seedling.id
AND seedling.parent_id = #{seedlingId} AND seedling.type = #{type}
AND seedling.id = #{seedlingId} AND seedling.type = #{type}

SELECT record.*, germplasm.name as germplasm_name, seedling.code as seedling_code, seedling.code_one as seedling_code_one, seedling.code_two as seedling_code_two FROM os_seedling_record as record left join sys_user u on u.user_id = record.create_by_id left join sys_dept d on u.dept_id = d.dept_id, os_germplasm as germplasm, os_seedling as seedling WHERE record.germplasm_id = germplasm.id AND record.seedling_id = seedling.id
AND record.germplasm_id = #{germplasmId}
AND record.seedling_id = #{seedlingId}
AND record.create_time >= #{startTime}
AND record.create_time <= #{endTime}
${dataScope} ORDER BY record.id DESC
